INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52787,null,null,null,'Line an 8-inch-square baking pan with wax paper or foil, and coat with non-stick spray.In a microwave-safe bowl, combine the dark chocolate chips, heavy cream and half of the sweetened condensed milk. Microwave the dark chocolate mixture in 20-second intervals, stirring in between each interval, until the chocolate is melted.Add the vanilla extract to the dark chocolate mixture and stir well until smooth.Transfer the dark chocolate mixture into the prepared pan and spread into an even layer.In a separate bowl, combine the white chocolate chips with the remaining half of the sweetened condensed milk. Microwave the white chocolate mixture in 20-second intervals, stirring in between each interval, until the chocolate is melted.Evenly spread the white chocolate mixture on top of dark chocolate layer.Top the chocolate layers with the Mallow Bits or miniature marshmallows, and gently press them down.Refrigerate for 4 hours, or until set.Remove the fudge and wax paper from the pan. Carefully peel all of the wax paper from the fudge.Cut the fudge into bite-sized pieces and serve.','Hot Chocolate Fudge','https://www.themealdb.com/images/media/meals/xrysxr1483568462.jpg','2 cups','2 tbs','1 – 14-ounce can','1 tsp','1-⅓ cups','1-½ cups','','','','','','','','','','','','','','','','Snack,Chocolate','https://www.youtube.com/watch?v=oJvbsVSblfk',1,4);
