INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52966,null,null,null,'Grease and line a 20 x 20cm/8" x 8" baking tin with parchment paper.Put the mars bars and butter in a heat proof bowl and place over a pan of barely simmering water. Mixing with a whisk, melt until the mixture is smooth.Pour over the rice krispies in a mixing bowl and mix until all the ingredients are evenly combined. Press evenly into the prepare baking tin and set aside.Melt the milk chocolate in the microwave or over a pan of barely simmering water.Spread the melted chocolate over the rice krispie mixture and leave to set in a cool place. Once set slice into squares and serve!','Chocolate Caramel Crispy','https://www.themealdb.com/images/media/meals/1550442508.jpg','6 chopped','150g','120g','150g',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','http://www.donalskehan.com/recipes/chocolate-caramel-rice-crispy-treats/','Sweet,Snack,Treat,Tart,Alcoholic,BBQ','https://www.youtube.com/watch?v=qsk_At_gjv0',2,4);
