INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52928,null,null,null,'In the bowl of a stand mixer, add warm water, a big pinch of sugar and yeast. Allow to sit until frothy.Into the same bowl, add 1/2 cup sugar, warm milk, melted butter, eggs and salt, and whisk until combined.Place a dough hook on the mixer, add the flour with the machine on, until a smooth but slightly sticky dough forms.Place dough in a bowl, cover with plastic wrap, and allow to proof for 1 1/2 hours.Cut dough into 12 pieces, and roll out into long oval-like shapes about 1/4 inch thick that resemble a beaver’s tail.In a large, deep pot, heat oil to 350 degrees. Gently place beavertail dough into hot oil and cook for 30 to 45 seconds on each side until golden brown.Drain on paper towels, and garnish as desired. Toss in cinnamon sugar, in white sugar with a squeeze of lemon, or with a generous slathering of Nutella and a handful of toasted almonds. Enjoy!','BeaverTails','https://www.themealdb.com/images/media/meals/ryppsv1511815505.jpg','1/2 cup ','2 parts ','1/2 cup ','1/2 cup ','6 tblsp','2','1 ½ tsp','2-1/2 cups','for frying','garnish','garnish','garnish','','','','','','','','','https://www.tastemade.com/videos/beavertails','Treat,Pudding,Speciality','https://www.youtube.com/watch?v=2G07UOqU2e8',3,4);
