INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52965,null,null,null,'Before you do anything, freeze your bacon slices that way when you"re ready to prep, it"ll be so much easier to chop!Wash the potatoes and cut medium dice into square pieces. To prevent any browning, place the already cut potatoes in a bowl filled with water.In the meantime, heat 1-2 tablespoons of oil in a large skillet over medium-high heat. Tilt the skillet so the oil spreads evenly.Once the oil is hot, drain the potatoes and add to the skillet. Season with salt, pepper, and Old Bay as needed.Cook for 10 minutes, stirring the potatoes often, until brown. If needed, add a tablespoon more of oil.Chop up the bacon and add to the potatoes. The bacon will start to render and the fat will begin to further cook the potatoes. Toss it up a bit! The bacon will take 5-6 minutes to crisp.Once the bacon is cooked, reduce the heat to medium-low, add the minced garlic and toss. Season once more. Add dried or fresh parsley. Control heat as needed.Let the garlic cook until fragrant, about one minute.Just before serving, drizzle over the maple syrup and toss. Let that cook another minute, giving the potatoes a caramelized effect.Serve in a warm bowl with a sunny side up egg!','Breakfast Potatoes','https://www.themealdb.com/images/media/meals/1550441882.jpg','3 Medium','1 tbs','2 strips','Minced','1 tbs','Garnish','Pinch','Pinch','To taste',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','http://www.vodkaandbiscuits.com/2014/03/06/bangin-breakfast-potatoes/','Breakfast,Brunch,','https://www.youtube.com/watch?v=BoD0TIO9nE4',3,2);
