INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52781,null,null,null,'Heat the oven to 180C/350F/gas mark 4. Drain and rinse the soaked wheat, put it in a medium pan with lots of water, bring to a boil and simmer for an hour, until cooked. Drain and set aside.Season the lamb with a teaspoon of salt and some black pepper. Put one tablespoon of oil in a large, deep sauté pan for which you have a lid; place on a medium-high heat. Add some of the lamb – don"t overcrowd the pan – and sear for four minutes on all sides. Transfer to a bowl, and repeat with the remaining lamb, adding oil as needed.Lower the heat to medium and add a tablespoon of oil to the pan. Add the shallots and fry for four minutes, until caramelised. Tip these into the lamb bowl, and repeat with the remaining vegetables until they are all nice and brown, adding more oil as you need it.Once all the vegetables are seared and removed from the pan, add the wine along with the sugar, herbs, a teaspoon of salt and a good grind of black pepper. Boil on a high heat for about three minutes.Tip the lamb, vegetables and whole wheat back into the pot, and add the stock. Cover and boil for five minutes, then transfer to the oven for an hour and a half.Remove the stew from the oven and check the liquid; if there is a lot, remove the lid and boil for a few minutes.','Irish stew','https://www.themealdb.com/images/media/meals/sxxpst1468569714.jpg','300g soaked overnight in water','2kg cut into 3cm cubes','120ml','24 Skinned','4 large','2','1','350g','150ml','1 tsp','4 sprigs','4 sprigs','450ml','','',null,null,null,null,null,'http://www.ottolenghi.co.uk/recipes/meat/irish-stew-shop','Stew,Meat','https://www.youtube.com/watch?v=kYH2qJXnSMo',11,1);
