INSERT INTO TB_MEAL (ID_MEAL,DATE_MODIFIED,STR_DRINK_ALTERNATE,STR_IMAGE_SOURCE,STR_INSTRUCTIONS,STR_MEAL,STR_MEAL_THUMB,STR_MEASURE1,STR_MEASURE2,STR_MEASURE3,STR_MEASURE4,STR_MEASURE5,STR_MEASURE6,STR_MEASURE7,STR_MEASURE8,STR_MEASURE9,STR_MEASURE10,STR_MEASURE11,STR_MEASURE12,STR_MEASURE13,STR_MEASURE14,STR_MEASURE15,STR_MEASURE16,STR_MEASURE17,STR_MEASURE18,STR_MEASURE19,STR_MEASURE20,STR_SOURCE,STR_TAGS,STR_YOUTUBE,STR_AREA_ID,STR_CATEGORY_ID) VALUES (52838,null,null,null,'Heat the oil in a large pan. Add the duck legs and brown on all sides for about 10 mins. Remove to a plate and set aside. Add the onions to the pan and cook for 5 mins until softened. Add the garlic and cook for a further 1 min, then stir in the cinnamon and flour and cook for a further min. Return the duck to the pan, add the wine, tomatoes, stock, herbs, sugar and seasoning. Bring to a simmer, then lower the heat, cover with a lid and cook for 2 hrs, stirring every now and then.Carefully lift the duck legs out of the sauce and place on a plate – they will be very tender so try not to lose any of the meat. Pull off and discard the fat, then shred the meat with 2 forks and discard the bones. Add the meat back to the sauce with the milk and simmer, uncovered, for a further 10-15 mins while you cook the pasta.Cook the pasta following pack instructions, then drain, reserving a cup of the pasta water, and add the pasta to the ragu. Stir to coat all the pasta in the sauce and cook for 1 min more, adding a splash of cooking liquid if it looks dry. Serve with grated Parmesan, if you like.','Venetian Duck Ragu','https://www.themealdb.com/images/media/meals/qvrwpt1511181864.jpg','1 tbls','4','2 finely chopped','2 cloves minced','2 tsp ground','2 tsp','250ml','800g','1','3 sprigs','2','1 tsp ','2 tbs','600g','Grated','','','','','','https://www.bbcgoodfood.com/recipes/venetian-duck-ragu',null,'https://www.youtube.com/watch?v=oWQDVgjB_Lw',12,8);
